import React from 'react'
import "./register.css"
import {Link} from 'react-router-dom'
import {useForm} from 'react-hook-form'




const Register = () => {

    const {register, handleSubmit}=useForm();
    const onSubmit=({firstName,lastName,email,password})=>{
    const errors=[];
    // cek firstName
    if(!firstName.length)
    errors.push({
        name:'fisrtName',
        message:"Nama awal harus diisi"
    })
    // cek firstName
    if(!lastName.length)
    errors.push({
        name:'lastName',
        message:"Nama akhir harus diisi"
    })

    // cek email required 
    if(!email.length)
    errors.push({
      name:'email',
      message:"email harus diisi"
    })

    // cek email valid
    if(!email.includes('@'))
    errors.push({
      name:'email',
      message:"email tidak valid"
    })


    // cek password required & panjang tulisan 8 huruf
    if(password.length < 8)
    errors.push({
      name:'password',
      message:"karakter harus lebih sama dengan 8 huruf"
    })

    // tampilkan di console jika invalid
    console.log({
      status:errors.length?422:200,
      message:errors.length?"terjadi kesalahan input!":"Anda berhasil Login!",
      errors
    })
  
    }

  return (
    <div className="register-page">
        <form onSubmit={handleSubmit(onSubmit)}>
        <h2>Create your Account</h2>
        <div className="container">
            <label htmlFor='firstName'><b>First Name</b></label>
            <input type="text" id='firstName' {...register('firstName')}placeholder="Joko" />
            <label htmlFor='lastName'><b>Last Name</b></label>
            <input type="text" id='lastName' {...register('lastName')} placeholder="Shidiq" />
            <label htmlFor='email'><b>Email Address</b></label>
            <input type="text" id='email' {...register('email')}placeholder="example@email.com" />
            <label htmlFor='password'><b>Password</b></label>
            <input type="password" id='password'{...register('password')}placeholder="Enter Password"></input>
            <div className="check">
                <input type="checkbox" />
                <label htmlFor="checkbox"> to register with us please tick to agree to our <a href=" ">Terms and Conditions</a>.</label>
            </div>
            <button type="submit">Register</button>
        </div>
        </form>
        <div className="container">
        <p>Alredy have an account? <Link to='/'>Sign In</Link></p>
        </div> 
    </div>
  )
}

export default Register
