import React from 'react'
import { useForm } from 'react-hook-form'
import "./login.css"

function Login() {

  const {register, handleSubmit}=useForm();

  const onSubmit=({email,password})=>{
// variabel validasi
const errors=[];
console.log(email,password,email.length)
    // cek email required 
    if(!email.length)
    errors.push({
      name:'email',
      message:"email harus diisi"
    })

    // cek email valid
    if(!email.includes('@'))
    errors.push({
      name:'email',
      message:"email tidak valid"
    })

    // cek password required & panjang tulisan 8 huruf
    if(!password.length >= 8)
    errors.push({
      name:'password',
      message:"karakter harus lebih sama dengan 8 huruf"
    })

    // tampilkan di console jika invalid
    console.log({
      status:errors.length?422:200,
      message:errors.length?"terjadi kesalahan input!":"Anda berhasil Login!",
      errors
    })
  }
  return (
    <div className="login-page">
      <form onSubmit={handleSubmit(onSubmit)}>
      <h2>Login to your Account</h2>
        <div className="container">
          <label htmlFor='email'><b>Email Address</b></label>
          <input type="text" id="email" {...register('email')} placeholder="example@email.com" />
          <label htmlFor='password'><b>Password</b></label>
          <input type="password" id="password" {...register('password')} placeholder="Enter Password" ></input>
          <button type="submit">Login</button>
        </div>
      </form>
      <div className="container">
        <p>dont have any account? <a href=''>Register</a></p>
        <p>Forggoten your password? <a href=''>Password</a></p>
      </div> 
    </div>
    
  )
}

export default Login
