import React, { useState } from "react";
import { useForm } from "react-hook-form";
import "./login.css";
import { Link } from "react-router-dom";

function Login() {
  const { register, handleSubmit } = useForm();

  // variabel validasi
  const [errors, setErrors] = useState([]);

  const onSubmit = ({ email, password }) => {
    const newErrors = [];

    // cek email required
    if (!email.length)
      newErrors.push({
        name: "email",
        message: "email harus diisi",
      });

    // cek email valid
    if (!email.includes("@"))
      newErrors.push({
        name: "email",
        message: "email tidak valid",
      });

    // cek password required & panjang tulisan 8 huruf
    if (password.length < 8)
      newErrors.push({
        name: "password",
        message: "karakter harus lebih sama dengan 8 huruf",
      });

    // tampilkan di console jika invalid
    console.log({
      status: errors.length ? 422 : 200,
      message: errors.length
        ? "terjadi kesalahan input!"
        : "Anda berhasil Login!",
      errors,
    });

    setErrors(newErrors);
  };

  const forgot = (e) => {
    e.preventDefault();
    alert("belum tersedia!");
  };
  return (
    <div className="login-page">
      <form onSubmit={handleSubmit(onSubmit)}>
        <h2>Login to your Account</h2>
        <div className="container">
          <label htmlFor="email">
            <b>Email Address</b>
          </label>
          <input
            type="text"
            id="email"
            {...register("email")}
            placeholder="example@email.com"
          />
          {/* message */}
          {errors.find((e) => e.name == "email") && (
            <span>{errors.find((e) => e.name == "email").message}</span>
          )}

          <label htmlFor="password">
            <b>Password</b>
          </label>
          <input
            type="password"
            id="password"
            {...register("password")}
            placeholder="Enter Password"
          >
            {/* message */}
            {errors.find((e) => e.name == "password") && (
              <span>{errors.find((e) => e.name == "password").message}</span>
            )}
          </input>
          <button type="submit">Login</button>
        </div>
      </form>
      <div className="container">
        <p>
          dont have any account? <Link to="/register">Register</Link>
        </p>
        <p>
          Forggoten your password?{" "}
          <a className="btn-links" onClick={forgot}>
            Password
          </a>
        </p>
      </div>
    </div>
  );
}

export default Login;
